        }
        $user = new ModelUser($req->data[ModelUser::USER_KEYNAME_ID], new ModelGroup($req->data[ModelUser::USER_KEYNAME_GROUP]));
        if($this->service->updateUser($user)) {
            return new HTTPOKResponse("user updated", $req->data);
